import React, {Component} from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const Post = props => (
    <tr>
        <td>{props.post.post_id}</td>
        <td>{props.post.post_author}</td>
        <td>{props.post.post_status}</td>
        <td>{props.post.post_title}</td>
        <td>{props.post.post_category}</td>
        <td>{props.post.post_comment_count}</td>
        <td><img width='100' src={props.post.post_image} alt={props.post.post_image} className='img-fluid'/></td>
        <td>{props.post.post_content}</td>
        <td>{props.post.post_date.substring(0,10)}</td>
        <td>
            <Link to={'/admin/Edit_post/'+props.post._id}>edit</Link> | <a href='#' onClick={()=>{props.deletePost(props.post._id)}}>delete</a>
        </td>
    </tr>
);

export default class ViewPost extends Component{
    constructor(props){
        super(props);

        this.deletePost = this.deletePost.bind(this);

        this.state = { posts: [] };
    }

    componentDidMount(){
        axios.get('http://localhost:5001/posts/')
        .then(response => {
            this.setState({
                posts: response.data
            });
            // alert(response.data);
        })
        .catch((error) => console.log(error));
    }

    deletePost(id){
        axios.delete('http://localhost:5001/posts/'+id)
        .then(res => console.log(res.data));
            this.setState({
                posts: this.state.posts.filter(el => el._id !== id)
            })
        .catch((error) => console.log(error));
    }

    postList(){
        return this.state.posts.map(currentpost => {
            return <Post post={currentpost} deletePost={this.deletePost} key={currentpost._id}/>;
        })
    }

    render(){
        return(
            <div>
                <h3>Logged Exercises</h3>
                <table className='table'>
                    <thead className='thead-light'>
                        <tr>
                            <th>Id</th>
                            <th>Author</th>
                            <th>Status</th>
                            <th>Title</th>
                            <th>Category</th>
                            <th>Comment count</th>
                            <th>Image</th>
                            <th>Content</th>
                            <th>Date</th>
                            <th>&nbsp;</th>
                            <th>&nbsp;</th>
                        </tr>
                    </thead>
                    <tbody>
                        { this.postList() }
                    </tbody>
                </table>
            </div>
        )
    }
}
