import React, {Component} from 'react';
import axios from 'axios';

// import {Link} from 'react-router-dom';

export default class AddCategory extends Component{
    constructor(props){
        super(props);

        this.onChangeCategory = this.onChangeCategory.bind(this);
        this.onPost = this.onPost.bind(this);

        this.state={
            post_category: '',
            post_categories: []
        }
    }
    componentDidMount(){
        axios.get('http://localhost:5000/categories/')
        .then(response => {
            if (response.data.length > 0){
                this.setState({
                    post_categories: response.data.map(cat=>cat.category),
                    post_category: response.data[0].category
                });
            } else console.log('no data found');
        });
    }

    onChangeCategory(e){
        this.setState({
            post_category: e.target.value
        });
        // console.log(e.target.value);
    }
   
    
    onSubmit(e){
        e.preventDefault();
    }

    onPost(){
        // var path = require("path");
        // var postfile = this.state.post_file.split('\\').join('/');
        // var file = path.basename(postfile);
        const category = {
            post_category: this.state.post_category
        };
        console.log(category);
        
        // console.log(posts);
        // const File_path = './../../public/files/' + posts.post_file;
        // const Img_path = './../../public/images/' + posts.post_image;
        
        // const file = new File(File_path);

        // alert(File_path);
        const formdata = new FormData();
        // formdata.append('post_title', this.state.post_title);
        formdata.append('post_category', this.state.post_category);
        
        console.log(formdata);
        
        axios.post('http://localhost:5001/categories/add', formdata)
        .then(res => {console.log(res.data);alert(res.data);})
        .catch(err => alert('err: ' + err));

        // window.location = '/';
    }

    render(){
        return(
            <form encType="multipart/form-data" onSubmit={this.onSubmit}>
                
                <div className="form-group">
                    <label htmlFor="title">Post Category</label>
                    <input type="text" className="form-control" name="post_author" required value={this.state.post_category} onChange={this.onChangeCategory} />
                </div>

                <div className="form-group">
                    <button className="btn btn-primary" type='submit' onClick={this.onPost} >Add Category</button>
                </div>
            </form>
        )
    }
}
