import React, {Component} from 'react';
import axios from 'axios';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
// import {Link} from 'react-router-dom';

export default class AddPost extends Component{
    constructor(props){
        super(props);

        this.onChangeTitle = this.onChangeTitle.bind(this);
        this.onChangeCategory = this.onChangeCategory.bind(this);
        this.onChangeAuthor = this.onChangeAuthor.bind(this);
        this.onChangeStatus = this.onChangeStatus.bind(this);
        this.onChangeImage = this.onChangeImage.bind(this);
        this.onChangeFile = this.onChangeFile.bind(this);
        this.onChangeContent = this.onChangeContent.bind(this);
        this.onChangeDate = this.onChangeDate.bind(this);

        this.state={
            post_title: '',
            post_category: '',
            post_author: '',
            post_status: '',
            post_image: '',
            post_file: '',
            post_content: '',
            post_date: new Date()
        }
    }

    onChangeTitle(e){
        this.setState({
            post_title: e.target.value
        });
    }

    onChangeCategory(e){
        this.setState({
            post_ategory: e.target.value
        });
    }
    onChangeAuthor(e){
        this.setState({
            post_author: e.target.value
        });
    }
    onChangeStatus(e){
        this.setState({
            post_status: e.target.value
        });
    }
    onChangeImage(e){
        this.setState({
            post_image: e.target.value
        });
    }
    onChangeFile(e){
        this.setState({
            post_file: e.target.value
        });
    }
    onChangeContent(e){
        this.setState({
            post_content: e.target.value
        });
    }
    onChangeDate(e){
        this.setState({
            post_date: e.target.value
        });
    }
    onSubmit(e){
        e.preventDefault();

        const exercise = {
            username: this.state.username,
            description: this.state.description,
            duration: this.state.duration,
            date: this.state.date
        };
        console.log(exercise);

        axios.post('http://localhost:5000/exercises/add', exercise)
        .then(res => console.log(res.data));

        window.location = '/';
    }

    render(){
        return(
            <form action="" encType="multipart/form-data" method="POST">
                <div className="form-group">
                    <label htmlFor="title">Post title</label>
                    <input type="text" className="form-control" name="post_title" />
                </div>
                <div className="form-group">
                    <label htmlFor="post_category">Post category</label>
                    <select name="post_category" id="">
                    <option value='News'>NEWS</option>
                    <option value='Audio'>AUDIO</option>
                    <option value='Video'>VIDEO</option>
                    </select>
                </div>
                <div className="form-group">
                    <label htmlFor="title">Post Author</label>
                    <input type="text" className="form-control" name="post_author" />
                </div>
                <div className="form-group">
                    <label htmlFor="title">Post status</label>
                    <input type="text" className="form-control" name="post_status" />
                </div>
                <div className="form-group custom-file">
                    <label htmlFor="post_image">Post image</label>
                    <input type="file" name="image" className="form-control custom-file-input" />
                </div>
                <div className="form-group custom-file mb-3">
                    <label htmlFor="title">Post vidoe/audio</label>
                    <input type="file" className="form-control custom-file-input" name="post_file" />
                </div>
                <div className="form-group">
                    <label htmlFor="title">Post content</label>
                    <textarea name="post_content" className="form-control" id="" cols="30" rows="10"></textarea>
                </div>
                <div className='form-group'>
                        <label>Post Date: </label>
                        <div>
                            <DatePicker
                            selected={this.state.post_date}
                            onChange={this.onChangeDate}
                            />
                        </div>
                    </div>
                <div className="form-group">
                    <button className="btn btn-primary" name="make_post">Post</button>
                </div>
            </form>
        )
    }
}
