import React, {Component} from 'react';
// import {Link} from 'react-router-dom';

export default class AddPost extends Component{
    render(){
        return(
            <form action="" encType="multipart/form-data" method="POST">
                <div className="form-group">
                    <label htmlFor="title">Post title</label>
                    <input type="text" className="form-control" name="post_title" />
                </div>
                <div className="form-group">
                    <label htmlFor="post_category">Post category</label>
                    <select name="post_category" id="">
                    {/* <?php
                    $connection = mysqli_connect("localhost","root","","music");
                        $query = "SELECT * FROM category";
                        $cat = mysqli_query($connection,$query);
                        if (!$cat) {
                        echo "data: error in query";
                        };
                        while($row = mysqli_fetch_array($cat)) {
                        $cat_id =$row['cat_id'];
                        $cat_title =$row['cat_title'];
                        echo '$cat_title';
                        echo "<option value='$cat_id'>$cat_title</option>";
                        }
                    ?> */}
                    </select>
                </div>
                <div className="form-group">
                    <label htmlFor="title">Post Author</label>
                    <input type="text" className="form-control" name="post_author" />
                </div>
                <div className="form-group">
                    <label htmlFor="title">Post tags</label>
                    <input type="text" className="form-control" name="post_tags" />
                </div>
                <div className="form-group">
                    <label htmlFor="title">Post status</label>
                    <input type="text" className="form-control" name="post_status" />
                </div>
                <div className="form-group custom-file">
                    <label for="post_image">Post image</label>
                    <input type="file" name="image" className="form-control custom-file-input" />
                </div>
                <div className="form-group custom-file mb-3">
                    <label htmlFor="title">Post vidoe/audio</label>
                    <input type="file" className="form-control custom-file-input" name="post_file" />
                </div>
                <div className="form-group">
                    <label htmlFor="title">Post content</label>
                    <textarea name="post_content" className="form-control" id="" cols="30" rows="10"></textarea>
                </div>
                <div className="form-group">
                    <button className="btn btn-primary" name="make_post">Post</button>
                </div>
            </form>
        )
    }
}
